type Slice struct {
	// N: the number of actual values in the array
	Length int
	// 10: the maximum number of values that can be stored in the array
	Capacity int
	// ["a", "b", "c"]: the actual values stored in the array
	Array [10]string
}

func main() {
	N := 100_000_000

	sl := make([]int, 0)

	hat := cap(sl)
	for i := 0; i < N; i++ {
		sl = append(sl, i)
		c := cap(sl)
		if c != hat {
			fmt.Println(hat, c)
		}
		hat = c
	}
}

func main() {
	// declare and initialize a slice of strings
	// with a length of 0 and capacity of 0
	a := []string{}

	// declare a variable of type slice of strings
	// with a length of 0 and capacity of 0
	var b []string

	// declare and initialize a slice of strings
	// with a length of 0 and capacity of 0
	c := make([]string, 0)

	fmt.Println(a)
	fmt.Println(b)
	fmt.Println(c)
