public static void main(String args[]) {
  try {
    // Open the file
    FileInputStream fstream = new FileInputStream("example.txt");

    // Get the object of DataInputStream
    DataInputStream in = new DataInputStream(fstream);
    BufferedReader br = new BufferedReader(new InputStreamReader(in));
    String strLine;

    // Read File Line By Line
    while ((strLine = br.readLine()) != null) {
      // Print the content on the console
      System.out.println(strLine);
    }

    // Close the input stream
    in.close();
  } catch (IOException e) {
    System.err.println("IO Error: " + e.getMessage());
  } catch (Exception e) {
    // Catch exception if any
    System.err.println("Error: " + e.getMessage());
  }
}

func readFile() error {

	// open the file
	f, err := os.Open("example.txt")
	if err != nil {
		return err
	}

	// close the file when done
	defer f.Close()

	// open buffered scanner with file
	scanner := bufio.NewScanner(f)

	// scan through each line of the file
	for scanner.Scan() {

		// print line to the console
		fmt.Println(scanner.Text())
	}

	// if there was an error while scanning
	// return the error to the calling function
	if err := scanner.Err(); err != nil {
		return err
	}

	// everything was good, return nil
	return nil
}

