func Test_Get(t *testing.T) {
	t.Parallel()

	act, err := Get("a")
	if err != nil {
		t.Fatalf("expect no error, got %s", err)
	}

	exp := "A"
	if act != exp {
		t.Fatalf("expected %s, got %s", exp, act)
	}

	_, err = Get("?")
	if err == nil {
		t.Fatalf("expected an error, got nil")
	}
}

