type matcher func(rune) bool

func sanitize(m matcher, s string) (string, error) {
	var val string

	// iterate over the runes in the string
	for _, c := range s {

		// call the matcher function
		// with the rune as the argument
		if m(c) {
			// append `*` to the result
			val = val + "*"
			// continue to the next rune
			continue
		}

		// append the rune to the result
		val = val + string(c)
	}

	// return the sanitized string
	return val, nil
}

func main() {

	// create a matcher function
	m := func(r rune) bool {
		// simulate doing something bad...
		panic("hahaha")

		// unreachable code
		return false
	}

	// sanitize the string
	s, err := sanitize(m, "go is awesome")
	if err != nil {
		// handle the error
		log.Fatal(err)
	}

	// print the sanitized string
	fmt.Println(s)
}

func sanitize(m matcher, s string) (val string, err error) {
	// guard against an invalid matcher that could panic
	defer func() {
		if e := recover(); e != nil {
			err = fmt.Errorf("invalid matcher. panic occurred: %v", e)
		}
	}()

	for _, c := range s {
		if m(c) {
			val = val + "*"
			continue
		}
		val = val + string(c)
	}
	return
}

func DoSomething(input int) error {
	switch input {
	case 0:
		// input was 0, return no error (nil)
		return nil
	case 1:
		// input was 1, panic with the string "one"
		panic("one")
	}

	// no case was matched
	return nil
}

func Test_DoSomething(t *testing.T) {
	t.Parallel()

	err := DoSomething(0)
	if err != nil {
		t.Fatal(err)
	}

	err = DoSomething(1)

	if err != nil {
		t.Fatal("expected nil, got", err)
	}
}

func DoSomething(input int) (err error) {
	// defer a function to recover from the panic
	defer func() {
		p := recover()
		if p == nil {
			// a nil was return, no panic was raised
			// return from the deferred function.
			return
		}

		// check if the recovered value is already an error
		if e, ok := p.(error); ok {
			// assign the recovered error to the perr variable
			// outside of the anonymous function scope
			err = e
			return
		}

		// a non-error value was recovered
		// create a new error, `ErrNonErrCaught`, with
		// information about the recovered value
		msg := fmt.Sprintf("non-error panic type %T %s", p, p)
		err = ErrNonErrCaught(msg)
	}()

	switch input {
	case 0:
		// input was 0, return no error (nil)
		return nil
	case 1:
		// input was 1, panic with the string "one"
		panic("one")
	}

	// no case was matched
	return nil
}

type ErrNonErrCaught string

func (e ErrNonErrCaught) Error() string {
	return string(e)
}

func Test_DoSomething(t *testing.T) {
	t.Parallel()

	err := DoSomething(0)
	if err != nil {
		t.Fatal(err)
	}

	err = DoSomething(1)

	if err != ErrNonErrCaught("non-error panic type string one") {
		t.Fatal("expected ErrNonErrCaught, got", err)
	}
}

