// Model is a key/value pair representing a model in the store.
// e.g. {"id": 1, "name": "bob"}
type Model map[string]any

// Store is a table based key/value store.
type Store struct {
	data map[string][]Model
}

// All returns all models in the store for the given table name.
// If no models exist, an error is returned.
func (s *Store) All(tn string) ([]Model, error) {
	db := s.data

	// if the underlying data is nil, return an error
	if db == nil {
		return nil, fmt.Errorf("no data")
	}

	// check to make sure table exists
	mods, ok := db[tn]

	// if table doesn't exist, return an error
	if !ok {
		return nil, fmt.Errorf("table %s not found", tn)
	}

	// return the slice of models
	return mods, nil
}

func Test_Store_All_NoTable(t *testing.T) {
	t.Parallel()

	s := &Store{
		data: map[string][]Model{},
	}

	_, err := s.All("users")
	if err == nil {
		t.Fatal("expected error, got nil")
	}

	exp := "table users not found"
	act := err.Error()
	if act != exp {
		t.Fatalf("expected %q, got %q", exp, act)
	}
}

type ErrTableNotFound struct {
	Table      string
	OccurredAt time.Time
}

func (e ErrTableNotFound) Error() string {
	return fmt.Sprintf("[%s] table not found %s", e.OccurredAt, e.Table)
}

// All returns all models in the store for the given table name.
// If no models exist, an error is returned.
func (s *Store) All(tn string) ([]Model, error) {
	db := s.data

	// if the underlying data is nil, return an error
	if db == nil {
		return nil, fmt.Errorf("no data")
	}

	// check to make sure table exists
	mods, ok := db[tn]

	// if table doesn't exist, return an error
	if !ok {
		return nil, ErrTableNotFound{
			Table:      tn,
			OccurredAt: time.Now(),
		}
	}

	// return the slice of models
	return mods, nil
}

func Test_Store_All_NoTable(t *testing.T) {
	t.Parallel()

	s := &Store{
		data: map[string][]Model{},
	}

	_, err := s.All("users")
	if err == nil {
		t.Fatal("expected error, got nil")
	}

	exp := "users"
	e, ok := err.(ErrTableNotFound)
	if !ok {
		t.Fatalf("expected ErrTableNotFound, got %T", err)
	}

	act := e.Table
	if act != exp {
		t.Fatalf("expected %q, got %q", exp, act)
	}

	if e.OccurredAt.IsZero() {
		t.Fatal("expected non-zero time")
	}
}

// All returns all models in the store for the given table name.
// If no models exist, an error is returned.
func (s *Store) All(tn string) ([]Model, error) {
	db := s.data

	// if the underlying data is nil, return an error
	if db == nil {
		return nil, fmt.Errorf("no data")
	}

	// check to make sure table exists
	mods, ok := db[tn]

	// if table doesn't exist, return an error
	if !ok {
		err := ErrTableNotFound{
			Table:      tn,
			OccurredAt: time.Now(),
		}
		return nil, fmt.Errorf("[Store.All] %w", err)
	}

	// return the slice of models
	return mods, nil
}

func Test_Store_All_NoTable(t *testing.T) {
	t.Parallel()

	s := &Store{
		data: map[string][]Model{},
	}

	_, err := s.All("users")
	if err == nil {
		t.Fatal("expected error, got nil")
	}

	exp := "users"
	e, ok := err.(ErrTableNotFound)
	if !ok {
		t.Fatalf("expected ErrTableNotFound, got %T", err)
	}

	act := e.Table
	if act != exp {
		t.Fatalf("expected %q, got %q", exp, act)
	}

	if e.OccurredAt.IsZero() {
		t.Fatal("expected non-zero time")
	}
}

type ErrorA struct {
	err error
}

func (e ErrorA) Error() string {
	return fmt.Sprintf("[ErrorA] %s", e.err)
}

type ErrorB struct {
	err error
}

func (e ErrorB) Error() string {
	return fmt.Sprintf("[ErrorB] %s", e.err)
}

type ErrorC struct {
	err error
}

func (e ErrorC) Error() string {
	return fmt.Sprintf("[ErrorC] %s", e.err)
}

// Wrapper wraps an error with a bunch of
// other errors.
// ex. Wrapper(original) #=> ErrorC -> ErrorB -> ErrorA -> original
func Wrapper(original error) error {
	original = ErrorA{original}
	original = ErrorB{original}
	original = ErrorC{original}
	return original
}

// WrapperLong wraps an error with a bunch of
// other errors.
// ex. WrapperLong(original) #=> ErrorC -> ErrorB -> ErrorA -> original
func WrapperLong(original error) error {
	return ErrorC{
		err: ErrorB{
			err: ErrorA{
				err: original,
			},
		},
	}
}

func Test_Unwrap(t *testing.T) {
	t.Parallel()

	original := errors.New("original error")
	wrapped := Wrapper(original)

	unwrapped := errors.Unwrap(wrapped)
	if unwrapped != original {
		t.Fatalf("expected %v, got %v", original, unwrapped)
	}

}

type Unwrapper interface {
	Unwrap() error
}

func (e ErrorA) Unwrap() error {
	return errors.Unwrap(e.err)
}

func (e ErrorA) Unwrap() error {
	if _, ok := e.err.(Unwrapper); ok {
		return errors.Unwrap(e.err)
	}

	return e.err
}

func Test_Store_All_NoTable(t *testing.T) {
	t.Parallel()

	s := &Store{
		data: map[string][]Model{},
	}

	_, err := s.All("users")
	if err == nil {
		t.Fatal("expected error, got nil")
	}

	// unwrap the error
	err = errors.Unwrap(err)

	exp := "users"
	e, ok := err.(ErrTableNotFound)
	if !ok {
		t.Fatalf("expected ErrTableNotFound, got %T", err)
	}

	act := e.Table
	if act != exp {
		t.Fatalf("expected %q, got %q", exp, act)
	}

	if e.OccurredAt.IsZero() {
		t.Fatal("expected non-zero time")
	}
}

// Wrapper wraps an error with a bunch of
// other errors.
// ex. Wrapper(original) #=> ErrorC -> ErrorB -> ErrorA -> original
func Wrapper(original error) error {
	original = ErrorA{original}
	original = ErrorB{original}
	original = ErrorC{original}
	return original
}

type AsError interface {
	As(target any) bool
}

func (e ErrorA) As(target any) bool {
	ex, ok := target.(*ErrorA)
	if !ok {
		// if the target is not an ErrorA,
		// pass the underlying error up the chain
		// by calling errors.As with the underlying error
		// and the target error
		return errors.As(e.err, target)
	}

	// set the target to the current error
	(*ex) = e
	return true
}

func Test_As(t *testing.T) {
	t.Parallel()

	original := errors.New("original error")
	wrapped := Wrapper(original)

	act := ErrorA{}

	ok := errors.As(wrapped, &act)
	if !ok {
		t.Fatalf("expected %v to act as %v", wrapped, act)
	}

	if act.err == nil {
		t.Fatalf("expected non-nil, got nil")
	}

}

type IsError interface {
	Is(target error) bool
}

func (e ErrorA) Is(target error) bool {
	if _, ok := target.(ErrorA); ok {
		// return true if target is ErrorA
		return true
	}

	// if not, pass the underlying error up the chain
	// by calling errors.Is with the underlying error
	// and the target error
	return errors.Is(e.err, target)
}

func Test_Is(t *testing.T) {
	t.Parallel()

	original := errors.New("original error")
	wrapped := Wrapper(original)

	exp := ErrorB{}

	ok := errors.Is(wrapped, exp)
	if !ok {
		t.Fatalf("expected %v to be %v", wrapped, exp)
	}

}

